"use strict";(self.webpackChunkthink_allof_yours_github_io=self.webpackChunkthink_allof_yours_github_io||[]).push([[8749],{1895:n=>{n.exports=JSON.parse('{"archive":{"blogPosts":[{"id":"php-legacy","metadata":{"permalink":"/blog/php-legacy","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2025-01-01-PHPLegacy.md","source":"@site/blog/2025-01-01-PHPLegacy.md","title":"PHP \ucf54\ub4dc\uc758 \uc608\uce21 \ubd88\uac00\ub2a5\ud55c \ub3d9\uc791","description":"\ub3c4\uc804: PHP \ucf54\ub4dc\uc758 \uc608\uce21 \ubd88\uac00\ub2a5\ud55c \ub3d9\uc791 \ud83c\udfaf","date":"2025-01-01T00:00:00.000Z","tags":[{"inline":false,"label":"PHP","permalink":"/blog/tags/php","description":"PHP related posts"},{"inline":false,"label":"Performance","permalink":"/blog/tags/performance","description":"Performance optimization related posts"},{"inline":false,"label":"Refactoring","permalink":"/blog/tags/refactoring","description":"Code refactoring related posts"},{"inline":false,"label":"Legacy Code","permalink":"/blog/tags/legacy-code","description":"Legacy code related posts"}],"readingTime":6.305,"hasTruncateMarker":false,"authors":[{"name":"Bundaberg Man","title":"Maintainer of Docusaurus","url":"https://github.com/bundabergman","page":{"permalink":"/blog/authors/all-bundaberg-man-articles"},"socials":{"x":"https://x.com/bundabergman","linkedin":"https://www.linkedin.com/in/bundabergman/","github":"https://github.com/bundabergman"},"imageURL":"https://github.com/bundabergman.png","key":"bundabergman"}],"frontMatter":{"slug":"php-legacy","title":"PHP \ucf54\ub4dc\uc758 \uc608\uce21 \ubd88\uac00\ub2a5\ud55c \ub3d9\uc791","authors":["bundabergman"],"tags":["php","performance","refactoring","legacy-code"]},"unlisted":false,"nextItem":{"title":"Welcome","permalink":"/blog/welcome"}},"content":"## **\ub3c4\uc804: PHP \ucf54\ub4dc\uc758 \uc608\uce21 \ubd88\uac00\ub2a5\ud55c \ub3d9\uc791** \ud83c\udfaf\\n\\n> 10\ub144\ucc28 \uac1c\ubc1c\uc790\uc778 \uc800\ub3c4 \ucf54\ub529\uc744 \ud558\ub2e4\uac00 \uc790\uc8fc \ubb38\uc81c\uc5d0 \ubd80\ub52a\ud799\ub2c8\ub2e4.\\n\\n\uc774\ubc88\uc5d0\ub294 \uc5d1\uc140 \ud30c\uc77c\uc5d0\uc11c \ub370\uc774\ud130\ub97c \ucc98\ub9ac\ud558\ub294 PHP \uc2a4\ud06c\ub9bd\ud2b8\uac00 \uc608\uce21 \ubd88\uac00\ub2a5\ud558\uac8c \ub3d9\uc791\ud558\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud588\ub2e4.\\n\\n\uc2ec\uc0ac \ub9c8\uac10\uc774 \uc5bc\ub9c8 \ub0a8\uc9c0 \uc54a\uc740 \uc0c1\ud669\uc5d0\uc11c \uc624\ub298 \uc624\uc804 9\uc2dc\uc5d0 \ub4e4\uc5b4\uc628 \uc694\uccad\uc778\ub370\\n\uc801\uc5b4\ub3c4 \uc624\ud6c4 2-3\uc2dc \uc0ac\uc774\uc5d0 \ubb38\uc81c\ub97c \ud30c\uc545\ud574\uc11c \uc218\uc815\ud558\ub294 \ucf54\ub4dc\ub97c \ubc30\ud3ec\ud558\ub294 \uac83\uc774 \uc911\uc694\ud55c\ub370\\n_\uc640 \uc624\ub298 \uc624\ud6c4 \ub9c8\uac10\uc778\ub370 \uc774\uc81c \uc694\uccad\uc774...?_ \u23f0\\n\\n### \uc774 \ubb38\uc81c\uc758 \uc6d0\uc778\uc744 \ucc3e\ub294 \uac83\uc774 \uc911\uc694 \ud83d\udd0d\\n\\n\uc5d1\uc140\uc5d0 \uc5c5\ub85c\ub4dc\ub41c \ud30c\uc77c\ub85c \ub370\uc774\ud130\ub97c \uc785\ub825\ud558\ub294 \uae30\ub2a5\uc778\ub370\\n\uc5b4\ub5a8 \ub54c\ub294 \uc815\uc0c1\uc801\uc73c\ub85c \uc5c5\ub85c\ub4dc \ub418\uace0\\n\uc5b4\ub5a8 \ub54c\ub294 \uc815\uc0c1\uc801\uc73c\ub85c \uc5c5\ub85c\ub4dc \ub418\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \uc0dd\uacbc\ub2e4.\\n\ud504\ub85c\uadf8\ub798\uba38\ub85c \uc774\ub7f0 \ubb38\uc81c\ub97c \ub9cc\ub098\uba74 \uc815\ub9d0 \ub2f9\ud669\uc2a4\ub7fd\ub2e4 \uc0c1\ud669\uc5d0 \ub530\ub77c \ub418\uace0 \uc548\ub418\uace0 \ud55c\ub2e4\ub2c8? \ub611\uac19\uc740 \ucf54\ub4dc\uc5d0\uc11c..?? \ud83d\ude31\\n\\n```sql\\n# \uac19\uc740 \ud504\ub85c\uc138\uc2a4\uc778\ub370 \uc800\uc7a5 \ub41c \uac12\uc774 \uc544\ub798 \ubd80\ubd84\uacfc \uc717 \ubd80\ubd84\uc774 \ub2e4\ub974\ub2e4.\\n# \uac70\uc758 \uc228\uc740\uadf8\ub9bc \ucc3e\uae30 \uc218\uc900 \uc798 \ucc3e\uc544\ubcf4\uba74 \ub2e4\ub978\uc810\uc774 \ubcf4\uc778\ub2e4.\\n# a:5:{i:1;a:2:{s:5:\\"check\\";i:0;s:4:\\"text\\";s:107:\\"\uc815\ub7c9\uc9c0\ud45c\ub294 \ubcf4\ud1b5 \uacc4\ub7c9\ud654\ub41c \uc218\uce58\uc758 \ud615\ud0dc\ub85c \ub098\ud0c0\ub0a0 \uc218 \uc788\ub294 \uc0dd\uc0b0\uc131 \uc9c0\ud45c\uc774\ub2e4.\\";}i:2;a:2:{s:5:\\"check\\";i:0;s:4:\\"text\\";s:130:\\"\uc815\uc131\uc9c0\ud45c\ub294 \ube44\uacc4\ub7c9\ud654\ub41c \ud615\ud0dc\ub85c \ub098\ud0c0\ub098\ubbc0\ub85c \uc5ed\ub7c9\uc744 \uae30\ubc18\uc73c\ub85c \ud3c9\uac00\ub97c \ud558\ub294 \uac83\uc774 \uc77c\ubc18\uc801\uc774\ub2e4.\\";}i:3;a:2:{s:5:\\"check\\";i:0;s:4:\\"text\\";s:150:\\"\uc870\uc9c1\uc758 \uc131\uacfc\ud3c9\uac00\ub294 \uac1d\uad00\uc801\uc774\uace0 \uacf5\uc815\ud574\uc57c \ud558\uba70 \uac00\uae09\uc801\uc774\uba70 \ud3c9\uac00\uc790\uc758 \uc8fc\uad00\uc801 \uc694\uc18c\uac00 \uac1c\uc785\ub418\uc5b4\uc11c\ub294 \uc548 \ub41c\ub2e4.\\";}i:4;a:2:{s:5:\\"check\\";i:1;s:4:\\"text\\";s:48:\\"\uc815\uc131\uc801\uc778 \uc9c0\ud45c\ub97c \ucd5c\ub300\ud654\ud574\uc57c \ud55c\ub2e4.\\";}i:5;a:2:{s:5:\\"check\\";i:0;s:4:\\"text\\";s:0:\\"\\";}}\\n\\n# a:5:{i:1;a:2:{s:5:\\"check\\";s:1:\\"0\\";s:4:\\"text\\";s:107:\\"\uc815\ub7c9\uc9c0\ud45c\ub294 \ubcf4\ud1b5 \uacc4\ub7c9\ud654\ub41c \uc218\uce58\uc758 \ud615\ud0dc\ub85c \ub098\ud0c0\ub0a0 \uc218 \uc788\ub294 \uc0dd\uc0b0\uc131 \uc9c0\ud45c\uc774\ub2e4.\\";}i:2;a:2:{s:5:\\"check\\";s:1:\\"0\\";s:4:\\"text\\";s:130:\\"\uc815\uc131\uc9c0\ud45c\ub294 \ube44\uacc4\ub7c9\ud654\ub41c \ud615\ud0dc\ub85c \ub098\ud0c0\ub098\ubbc0\ub85c \uc5ed\ub7c9\uc744 \uae30\ubc18\uc73c\ub85c \ud3c9\uac00\ub97c \ud558\ub294 \uac83\uc774 \uc77c\ubc18\uc801\uc774\ub2e4.\\";}i:3;a:2:{s:5:\\"check\\";s:1:\\"0\\";s:4:\\"text\\";s:150:\\"\uc870\uc9c1\uc758 \uc131\uacfc\ud3c9\uac00\ub294 \uac1d\uad00\uc801\uc774\uace0 \uacf5\uc815\ud574\uc57c \ud558\uba70 \uac00\uae09\uc801\uc774\uba70 \ud3c9\uac00\uc790\uc758 \uc8fc\uad00\uc801 \uc694\uc18c\uac00 \uac1c\uc785\ub418\uc5b4\uc11c\ub294 \uc548 \ub41c\ub2e4.\\";}i:4;a:2:{s:5:\\"check\\";s:1:\\"1\\";s:4:\\"text\\";s:48:\\"\uc815\uc131\uc801\uc778 \uc9c0\ud45c\ub97c \ucd5c\ub300\ud654\ud574\uc57c \ud55c\ub2e4.\\";}i:5;a:2:{s:5:\\"check\\";s:1:\\"0\\";s:4:\\"text\\";s:0:\\"\\";}}\\n```\\n\\n\ubb38\uc81c\uac00 \ub41c \ucf54\ub4dc\ub294 \uc544\ub798\uc640 \uac19\ub2e4.\\n\\n```php\\n        $rows[10] = @explode(\\",\\", $rows[10]);\\n\\n        $row[\'exb_answers\'] = array(\\n            1 => array(\'check\' => (in_array(1, $rows[10])) ? \'1\' : \'0\', \'text\' => sql_escape_string($rows[5])),\\n            2 => array(\'check\' => (in_array(2, $rows[10])) ? \'1\' : \'0\', \'text\' => sql_escape_string($rows[6])),\\n            3 => array(\'check\' => (in_array(3, $rows[10])) ? \'1\' : \'0\', \'text\' => sql_escape_string($rows[7])),\\n            4 => array(\'check\' => (in_array(4, $rows[10])) ? \'1\' : \'0\', \'text\' => sql_escape_string($rows[8])),\\n            5 => array(\'check\' => (in_array(5, $rows[10])) ? \'1\' : \'0\', \'text\' => sql_escape_string($rows[9])),\\n        );\\n        $row[\'exb_answers\']             = serialize($row[\'exb_answers\']);\\n```\\n\\n\ucc98\uc74c\uc5d0\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc124\uc815\uc5d0\uc11c \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud588\uc744 \uac70\ub77c\uace0 \uc0dd\uac01\ud588\ub294\ub370\\n\\n\ud558\uc9c0\ub9cc \uc11c\ube44\uc2a4 \uac04 \uc124\uc815\uc744 \ube44\uad50\ud574\ubcf8 \uacb0\uacfc, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uad6c\uc131\uc774 \uc77c\uad00\ub418\uac8c \uc720\uc9c0\ub418\uace0 \uc788\uc5c8\uace0,\\n\\n\ubb38\uc81c\ub294 PHP \ucf54\ub4dc \ub0b4\uc5d0\uc11c \ubb38\uc790\uc5f4 \uc774\uc2a4\ucf00\uc774\ud551\uc744 \ucc98\ub9ac\ud558\ub294 sql_escape_string \ud568\uc218\uc758 \uc0ac\uc6a9 \ubc29\uc2dd\uc5d0 \uc788\uc5c8\ub2e4.\\n\\n~~\ubd84\uba85\ud788 \uc5bc\ub9c8\uc804\uc5d0\ub294 \ubb38\uc81c\uac00 \uc5c6\uc5c8\ub294\ub370 \ucf54\ub4dc \uc218\uc815\ub3c4 \uc548\ud588\ub294\ub370 \uac11\uc790\uae30? \uc774\ub7f0 \ubb38\uc81c\uac00 \uc0dd\uae30\ub124\uc694???~~ \ud83e\udd14\\n\\n#### \ubb38\uc81c\uc758 \uc8fc\ubc94: sql_escape_string \ud568\uc218 \u26a0\ufe0f\\n\\n```php\\nfunction sql_escape_string($str) {\\n    global $connect_db;\\n    return ($connect_db, $str);\\n}\\n```\\n\\n\uc774 \ud568\uc218\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc5f0\uacb0\uc5d0 \ubb38\uc790 \uc778\ucf54\ub529\uc744 \uba85\uc2dc\uc801\uc73c\ub85c \uc124\uc815\ud558\uc9c0 \uc54a\uc558\uace0, \uc774\ub294 \ube44-ASCII \ubb38\uc790\ub4e4, \ud2b9\ud788 \ud55c\uae00 \ubb38\uc790\ub098 \ud2b9\uc218 \uc218\ud559\uae30\ud638 \ucc98\ub9ac\uc5d0 \uacb0\uc815\uc801\\n\\n#### **\ud574\uacb0\ucc45: \uc62c\ubc14\ub978 \ubb38\uc790 \uc778\ucf54\ub529 \uc124\uc815** \u2728\\n\\n\ubb38\uc81c\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574 sql_escape_string \ud568\uc218\ub97c \uc218\uc815\ud558\uc5ec \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc5f0\uacb0 \uc2dc UTF-8 \uc778\ucf54\ub529\uc744 \uba85\uc2dc\uc801\uc73c\ub85c \uc124\uc815\ud558\ub3c4\ub85d \ubcc0\uacbd.\\n\ubaa8\ub4e0 \ubb38\uc790\uc5f4\uc774 \uc62c\ubc14\ub974\uac8c \uc778\ucf54\ub529\ub418\ub3c4\ub85d \ubcf4\uc7a5\ud558\ub294 \uac83\uc774 \uc911\uc694\ud5c0\uc74c\\n\ub2e4\uc74c\uc740 \uc5c5\ub370\uc774\ud2b8\ub41c \ud568\uc218\\n\\n```php\\nfunction sql_escape_string($str) {\\n    global $connect_db;\\n    $connect_db->set_charset(\'utf8\');\\n    return mysqli_escape_string($connect_db, $str);\\n}\\n```\\n\\n#### \ud574\uacb0\ucc45 \uc120\ud0dd \uc774\uc720 \ud83d\udca1\\n\\n- \uc77c\uad00\ub41c \uc778\ucf54\ub529: \uac01 \ud638\ucd9c\ub9c8\ub2e4 \ubb38\uc790 \uc9d1\ud569\uc744 utf8\uc73c\ub85c \uc124\uc815\ud568\uc73c\ub85c\uc368 \ud2b9\ud788 \ud55c\uae00,\ud2b9\uc218\ubb38\uc790 \ub370\uc774\ud130 \ucc98\ub9ac\uc758 \uc77c\uad00\uc131\uc744 \ubcf4\uc7a5\\n- \uc720\uc5f0\uc131\uacfc \ubcf4\uc548: \uc774 \ud568\uc218\ub294 mysqli_escape_string\uc744 \uacc4\uc18d \uc0ac\uc6a9\ud558\uc9c0\ub9cc(mysqli_real_escape_string \uc0ac\uc6a9\uc744 \ub354 \uad8c\uc7a5\ud569\ub2c8\ub2e4), SQL \uc778\uc81d\uc158\uc5d0 \ub300\ud55c \ubcf4\uc548\uc744 \uc81c\uacf5\\n- \uc804\uc5ed \uc801\uc6a9: \uc774 \ubcc0\uacbd\uc740 \ud55c\uae00 \ubb38\uc790 \ubb38\uc81c \ud574\uacb0\ubfd0\ub9cc \uc544\ub2c8\ub77c \ub2e4\ub978 \ube44-ASCII \ubb38\uc790 \ucc98\ub9ac \ub2a5\ub825\uc744 \ud5a5\uc0c1\uc2dc\ucf1c \uc218\ud559\uae30\ud638 \ub4f1\uc758 \ud2b9\uc218\uae30\ud638 \uc0ac\uc6a9\uc5d0 \uc6a9\uc758\\n\\n#### \uacb0\ub860: \ud2b9\uc218\ubb38\uc790\ub97c \uc800\uc7a5\ud558\uace0 \ucf54\ub4dc\ub294 \ubb38\uc790 \uc778\ucf54\ub529\uc774 \ub9e4\uc6b0 \uc911\uc694\ud558\uace0 \uc624\ub958\ub97c \uc218\uc815\ud560 \ub54c\ub098 \uae30\ub2a5\uc744 \uad6c\ud604\ud560 \ub54c \uc81c\uc77c \uba3c\uc800 \uace0\ub824\ud574\uc57c \ud568 \ud83c\udf93\\n\\n\ud639\uc2dc\ub098 \uac19\uc740 \ubb38\uc81c\ub97c \uac00\uc9c0\uace0 \uc788\ub294 \uc0ac\ub78c\uc5d0\uac8c \ub3c4\uc6c0\uc774 \ub420\uae4c\ud558\uc5ec \uc62c\ub824 \ubd05\ub2c8\ub2e4.\\n\\n> \ud604\uc7ac \uac1c\ubc1c\ub418\uace0 \uc788\ub294 \uc0c8\ub85c\uc6b4 python \uae30\ubc18 \uc2dc\uc2a4\ud15c\uc774 \uc2ec\uc0ac\uac00 \uc798 \ud1b5\uacfc\ub420\ub54c\uae4c\uc9c0 \uc798 \uace0\uccd0\uc11c \uc368\ubd10\uc57c\uc9c0 \ud734... \ud83d\udc0d"},{"id":"welcome","metadata":{"permalink":"/blog/welcome","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2024-11-05-welcome/index.md","source":"@site/blog/2024-11-05-welcome/index.md","title":"Welcome","description":"\ub3cc\uace0 \ub3cc\uc544 Docusaurus \ube14\ub85c\uadf8\uc5d0 \uc815\ucc29\ud588\uc2b5\ub2c8\ub2e4","date":"2024-11-05T00:00:00.000Z","tags":[{"inline":false,"label":"Facebook","permalink":"/blog/tags/facebook","description":"Facebook tag description"}],"readingTime":0.09,"hasTruncateMarker":true,"authors":[{"name":"Bundaberg Man","title":"Maintainer of Docusaurus","url":"https://github.com/bundabergman","page":{"permalink":"/blog/authors/all-bundaberg-man-articles"},"socials":{"x":"https://x.com/bundabergman","linkedin":"https://www.linkedin.com/in/bundabergman/","github":"https://github.com/bundabergman"},"imageURL":"https://github.com/bundabergman.png","key":"bundabergman"}],"frontMatter":{"slug":"welcome","title":"Welcome","authors":["bundabergman"],"tags":["facebook"]},"unlisted":false,"prevItem":{"title":"PHP \ucf54\ub4dc\uc758 \uc608\uce21 \ubd88\uac00\ub2a5\ud55c \ub3d9\uc791","permalink":"/blog/php-legacy"},"nextItem":{"title":"\ud83c\udfa8 Fullpage.js + Next.js + TypeScript\ub85c \uba4b\uc9c4 \uc6f9\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30","permalink":"/blog/fullpage-js-nextjs-typescript-integration"}},"content":"## \ub3cc\uace0 \ub3cc\uc544 Docusaurus \ube14\ub85c\uadf8\uc5d0 \uc815\ucc29\ud588\uc2b5\ub2c8\ub2e4\\n\\n\x3c!-- truncate --\x3e"},{"id":"fullpage-js-nextjs-typescript-integration","metadata":{"permalink":"/blog/fullpage-js-nextjs-typescript-integration","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2024-04-12-Fullpagejs.md","source":"@site/blog/2024-04-12-Fullpagejs.md","title":"\ud83c\udfa8 Fullpage.js + Next.js + TypeScript\ub85c \uba4b\uc9c4 \uc6f9\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30","description":"Fullpage.js + Next.js + TypeScript\ub85c \uba4b\uc9c4 \uc6f9\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30 \ud83d\ude80","date":"2024-04-12T00:00:00.000Z","tags":[{"inline":true,"label":"fullpage.js","permalink":"/blog/tags/fullpage-js"},{"inline":true,"label":"nextjs","permalink":"/blog/tags/nextjs"},{"inline":true,"label":"typescript","permalink":"/blog/tags/typescript"},{"inline":true,"label":"react","permalink":"/blog/tags/react"},{"inline":true,"label":"frontend","permalink":"/blog/tags/frontend"}],"readingTime":3.755,"hasTruncateMarker":false,"authors":[{"name":"Bundaberg Man","title":"Maintainer of Docusaurus","url":"https://github.com/bundabergman","page":{"permalink":"/blog/authors/all-bundaberg-man-articles"},"socials":{"x":"https://x.com/bundabergman","linkedin":"https://www.linkedin.com/in/bundabergman/","github":"https://github.com/bundabergman"},"imageURL":"https://github.com/bundabergman.png","key":"bundabergman"}],"frontMatter":{"slug":"fullpage-js-nextjs-typescript-integration","title":"\ud83c\udfa8 Fullpage.js + Next.js + TypeScript\ub85c \uba4b\uc9c4 \uc6f9\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30","authors":["bundabergman"],"tags":["fullpage.js","nextjs","typescript","react","frontend"]},"unlisted":false,"prevItem":{"title":"Welcome","permalink":"/blog/welcome"},"nextItem":{"title":"\ud83d\ude80 PHP \ub808\uac70\uc2dc \ucf54\ub4dc \ub9ac\ud329\ud1a0\ub9c1 - 300\ubc30 \uc131\ub2a5 \uac1c\uc120 \uc0ac\ub840","permalink":"/blog/php-legacy"}},"content":"## Fullpage.js + Next.js + TypeScript\ub85c \uba4b\uc9c4 \uc6f9\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30 \ud83d\ude80\\n\\n\uc548\ub155\ud558\uc138\uc694! \uc624\ub298\uc740 Fullpage.js, Next.js, \uadf8\ub9ac\uace0 TypeScript\ub97c \ud568\uaed8 \uc0ac\uc6a9\ud558\uc5ec \uba4b\uc9c4 \uc6f9 \ud398\uc774\uc9c0\ub97c \uad6c\ud604\ud558\ub294 \ubc29\ubc95\uc744 \uacf5\uc720\ud558\ub824\uace0 \ud569\ub2c8\ub2e4. \uc778\ud130\ub137\uc5d0\uc11c \uc774 \uc138 \uac00\uc9c0 \uae30\uc220\uc744 \ud568\uaed8 \uc0ac\uc6a9\ud558\ub294 \uc608\uc81c\ub97c \ucc3e\uae30 \uc5b4\ub824\uc6cc \uc774\ub807\uac8c \ube14\ub85c\uadf8\uc5d0 \uc791\uc131\ud558\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n## \ud83d\udcda \ucc38\uace0 \uc790\ub8cc\\n\\n- [\uacf5\uc2dd Next.js \uc0d8\ud50c](https://github.com/alvarotrigo/react-fullpage/tree/master/examples/next)\\n- [\uc644\uc131\ub41c \ud504\ub85c\uc81d\ud2b8 GitHub](https://github.com/ThinkAllofYours/fullpage_nextjs_typescript.git)\\n\\n## \ud83d\udee0\ufe0f \uc900\ube44\ubb3c\\n\\n\uba3c\uc800, \ud504\ub85c\uc81d\ud2b8\uc5d0 \ud544\uc694\ud55c \ud328\ud0a4\uc9c0\ub4e4\uc744 \uc124\uce58\ud574\uc57c \ud569\ub2c8\ub2e4:\\n\\n```bash\\nnpx create-next-app@latest --typescript\\nnpm install react-bootstrap bootstrap\\nnpm install @fullpage/react-fullpage\\n```\\n\\n### \ud83d\udce6 package.json\\n\\n```json\\n{\\n  \\"name\\": \\"fullpage_nextjs_typescript\\",\\n  \\"version\\": \\"0.1.0\\",\\n  \\"private\\": true,\\n  \\"scripts\\": {\\n    \\"dev\\": \\"next dev\\",\\n    \\"build\\": \\"next build\\",\\n    \\"start\\": \\"next start\\",\\n    \\"lint\\": \\"next lint\\"\\n  },\\n  \\"license\\": \\"gpl-3.0\\",\\n  \\"dependencies\\": {\\n    \\"@fullpage/react-fullpage\\": \\"^0.1.38\\",\\n    \\"@types/node\\": \\"18.15.11\\",\\n    \\"@types/react\\": \\"18.0.34\\",\\n    \\"@types/react-dom\\": \\"18.0.11\\",\\n    \\"bootstrap\\": \\"^5.2.3\\",\\n    \\"next\\": \\"13.3.0\\",\\n    \\"react\\": \\"18.2.0\\",\\n    \\"react-bootstrap\\": \\"^2.7.2\\",\\n    \\"react-dom\\": \\"18.2.0\\",\\n    \\"typescript\\": \\"5.0.4\\"\\n  }\\n}\\n```\\n\\n## \ud83d\udcbb \uad6c\ud604\ud558\uae30\\n\\n> `components/fullpageExample.tsx`\ub77c\ub294 \ud30c\uc77c\uc744 \uc0dd\uc131\ud558\uace0 \uc544\ub798\uc758 \ucf54\ub4dc\ub97c \uc785\ub825\ud569\ub2c8\ub2e4.\\n\\n[\uc774\uc804 \ucf54\ub4dc\uc640 \ub3d9\uc77c\ud55c TypeScript \ucf54\ub4dc \ube14\ub85d]\\n\\n```tsx\\nimport React, { useState } from \\"react\\";\\nimport ReactFullpage, { fullpageOptions } from \\"@fullpage/react-fullpage\\";\\nimport { Navbar, Nav, Button } from \\"react-bootstrap\\";\\n\\ninterface Section {\\n  text: string;\\n  id?: number;\\n}\\n\\nconst originalColors = [\\n  \\"blue\\",\\n  \\"#0798ec\\",\\n  \\"#fc6c7c\\",\\n  \\"#435b71\\",\\n  \\"orange\\",\\n  \\"blue\\",\\n  \\"purple\\",\\n  \\"yellow\\",\\n];\\n\\ntype Credits = {\\n  enabled?: boolean;\\n  label?: string;\\n  position?: \\"left\\" | \\"right\\";\\n};\\n\\nconst pluginWrapper = () => {\\n  /*\\n   * require(\'../static/fullpage.scrollHorizontally.min.js\'); // Optional. Required when using the \\"scrollHorizontally\\" extension.\\n   */\\n};\\n\\nconst FullpageJsExample = () => {\\n  const [sectionsColor, setSectionsColor] = useState([...originalColors]);\\n  const [fullpages, setFullpages] = useState<Section[]>([\\n    {\\n      text: \\"Section 1\\",\\n    },\\n    {\\n      text: \\"Section 2\\",\\n    },\\n    {\\n      text: \\"Section 3\\",\\n    },\\n  ]);\\n\\n  const onLeave = (origin: any, destination: any, direction: any) => {\\n    console.log(\\"onLeave\\", { origin, destination, direction });\\n  };\\n\\n  const handleChangeColors = () => {\\n    const newColors =\\n      sectionsColor[0] === \\"yellow\\" ? [...originalColors] : [\\"yellow\\", \\"blue\\", \\"white\\"];\\n    setSectionsColor(newColors);\\n  };\\n\\n  const handleAddSection = () => {\\n    setFullpages((prevFullpages) => [\\n      ...prevFullpages,\\n      {\\n        text: `section ${prevFullpages.length + 1}`,\\n        id: Math.random(),\\n      },\\n    ]);\\n  };\\n\\n  const handleRemoveSection = () => {\\n    setFullpages((prevFullpages) => {\\n      const newPages = [...prevFullpages];\\n      newPages.pop();\\n      return newPages;\\n    });\\n  };\\n\\n  if (!fullpages.length) {\\n    return null;\\n  }\\n\\n  const Menu = () => (\\n    <Navbar bg=\\"light\\" expand=\\"lg\\" fixed=\\"top\\">\\n      <Navbar.Brand href=\\"#home\\" className=\\"mx-2\\">\\n        Fullpage.js + Next.js + Typescript\\n      </Navbar.Brand>\\n      <Navbar.Toggle aria-controls=\\"basic-navbar-nav\\" />\\n      <Navbar.Collapse id=\\"basic-navbar-nav\\">\\n        <Nav className=\\"mr-auto\\">\\n          <Nav.Item>\\n            <Button onClick={handleAddSection} className=\\"mr-2, mx-1\\">\\n              ADD SECTION\\n            </Button>\\n          </Nav.Item>\\n          <Nav.Item>\\n            <Button onClick={handleRemoveSection} className=\\"mr-2, mx-1\\">\\n              REMOVE SECTION\\n            </Button>\\n          </Nav.Item>\\n          <Nav.Item>\\n            <Button onClick={handleChangeColors}>CHANGE SECTION</Button>\\n          </Nav.Item>\\n        </Nav>\\n      </Navbar.Collapse>\\n    </Navbar>\\n  );\\n\\n  const credits: Credits = {\\n    enabled: true,\\n    label: \\"my custom\\",\\n    position: \\"left\\",\\n  };\\n\\n  return (\\n    <div className=\\"App\\">\\n      <Menu />\\n      <ReactFullpage\\n        licenseKey={\\"OPEN-SOURCE-GPLV3-LICENSE\\"}\\n        navigation\\n        onLeave={onLeave}\\n        sectionsColor={sectionsColor}\\n        pluginWrapper={pluginWrapper}\\n        debug={false}\\n        credits={credits}\\n        render={(comp: any) => (\\n          <ReactFullpage.Wrapper>\\n            {fullpages.map(({ text }) => (\\n              <div key={text} className=\\"section\\">\\n                <h1>{text}</h1>\\n              </div>\\n            ))}\\n          </ReactFullpage.Wrapper>\\n        )}\\n      />\\n    </div>\\n  );\\n};\\n\\nexport default FullpageJsExample;\\n```\\n\\n\ucf54\ub4dc \uc124\uba85\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4:\\n\\n1.  React \ubc0f \ud544\uc694\ud55c \ubaa8\ub4c8\uc744 import \ud569\ub2c8\ub2e4.\\n2.  \uac01 \uc139\uc158\uc758 \uc815\ubcf4\uc640 \uc0c9\uc0c1\uc744 \uc800\uc7a5\ud558\ub294 \uc0c1\ud0dc\ub97c \uc0dd\uc131\ud569\ub2c8\ub2e4.\\n3.  \uc139\uc158\uc744 \ucd94\uac00\ud558\uace0 \uc0ad\uc81c\ud558\uac70\ub098 \uc0c9\uc0c1\uc744 \ubcc0\uacbd\ud558\ub294 \ud568\uc218\ub97c \uad6c\ud604\ud569\ub2c8\ub2e4.\\n4.  React-Bootstrap\uc744 \uc0ac\uc6a9\ud558\uc5ec \uc0c1\ub2e8 \uba54\ub274\ub97c \uad6c\ud604\ud569\ub2c8\ub2e4.\\n5.  Fullpage.js\uc758 \uc635\uc158\uc744 \uc124\uc815\ud558\uace0, \uac01 \uc139\uc158\uc744 \ub80c\ub354\ub9c1\ud569\ub2c8\ub2e4.\\n\\n\uc2e4\ud589\\n--\\n\\n\uc774\uc81c \ud130\ubbf8\ub110\uc5d0\uc11c `npm run dev` \uba85\ub839\uc5b4\ub97c \uc785\ub825\ud558\uc5ec \uac1c\ubc1c \uc11c\ubc84\ub97c \uc2e4\ud589\uc2dc\ud0a4\uc138\uc694. \uc6f9 \ube0c\ub77c\uc6b0\uc800\uc5d0\uc11c `http://localhost:3000`\uc5d0 \uc811\uc18d\ud558\uba74 \uc791\uc131\ud55c \uc6f9 \ud398\uc774\uc9c0\ub97c \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc774\uc0c1\uc73c\ub85c Fullpage.js, Next.js, TypeScript\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc6f9 \ud398\uc774\uc9c0\ub97c \uad6c\ud604\ud558\ub294 \ubc29\ubc95\uc785\ub2c8\ub2e4."},{"id":"php-legacy","metadata":{"permalink":"/blog/php-legacy","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2023-12-04-PHPLegacy.md","source":"@site/blog/2023-12-04-PHPLegacy.md","title":"\ud83d\ude80 PHP \ub808\uac70\uc2dc \ucf54\ub4dc \ub9ac\ud329\ud1a0\ub9c1 - 300\ubc30 \uc131\ub2a5 \uac1c\uc120 \uc0ac\ub840","description":"\ud83d\udcdd \uac1c\uc694","date":"2023-12-04T00:00:00.000Z","tags":[{"inline":false,"label":"PHP","permalink":"/blog/tags/php","description":"PHP related posts"},{"inline":false,"label":"Performance","permalink":"/blog/tags/performance","description":"Performance optimization related posts"},{"inline":false,"label":"Refactoring","permalink":"/blog/tags/refactoring","description":"Code refactoring related posts"},{"inline":false,"label":"Legacy Code","permalink":"/blog/tags/legacy-code","description":"Legacy code related posts"}],"readingTime":4.565,"hasTruncateMarker":false,"authors":[{"name":"Bundaberg Man","title":"Maintainer of Docusaurus","url":"https://github.com/bundabergman","page":{"permalink":"/blog/authors/all-bundaberg-man-articles"},"socials":{"x":"https://x.com/bundabergman","linkedin":"https://www.linkedin.com/in/bundabergman/","github":"https://github.com/bundabergman"},"imageURL":"https://github.com/bundabergman.png","key":"bundabergman"}],"frontMatter":{"slug":"php-legacy","title":"\ud83d\ude80 PHP \ub808\uac70\uc2dc \ucf54\ub4dc \ub9ac\ud329\ud1a0\ub9c1 - 300\ubc30 \uc131\ub2a5 \uac1c\uc120 \uc0ac\ub840","authors":["bundabergman"],"tags":["php","performance","refactoring","legacy-code"]},"unlisted":false,"prevItem":{"title":"\ud83c\udfa8 Fullpage.js + Next.js + TypeScript\ub85c \uba4b\uc9c4 \uc6f9\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30","permalink":"/blog/fullpage-js-nextjs-typescript-integration"}},"content":"## \ud83d\udcdd \uac1c\uc694\\n\\n\ub808\uac70\uc2dc PHP \uc2dc\uc2a4\ud15c\uc758 \uc131\ub2a5 \uac1c\uc120 \uc0ac\ub840\ub97c \uacf5\uc720\ud569\ub2c8\ub2e4. **300\ubd84**\uc774 \uac78\ub9ac\ub358 \ub370\uc774\ud130 \ucc98\ub9ac \uc791\uc5c5\uc744 **5\ucd08**\ub85c \ub2e8\ucd95\ud55c \ub9ac\ud329\ud1a0\ub9c1 \uacfc\uc815\uc744 \uc124\uba85\ud569\ub2c8\ub2e4.\\n\\n## \ud83d\udd0d \ubb38\uc81c \uc0c1\ud669\\n\\n### \u274c \uae30\uc874 \uc2dc\uc2a4\ud15c\uc758 \ubb38\uc81c\uc810\\n\\n- 300\uba85\uc758 \ub370\uc774\ud130 \ucc98\ub9ac\uc5d0 \uc57d 300\ubd84 \uc18c\uc694 (1\uc778\ub2f9 1\ubd84)\\n- \uc6b4\uc601\ud300\uc758 \uc5c5\ubb34 \ud6a8\uc728\uc131 \uc800\ud558\\n- \ub370\uc774\ud130\uac00 \uc99d\uac00\ud560\uc218\ub85d \uae30\ud558\uae09\uc218\uc801\uc73c\ub85c \ucc98\ub9ac \uc2dc\uac04 \uc99d\uac00\\n\\n### \ud83e\udd14 \uc131\ub2a5 \uc800\ud558\uc758 \uc8fc\uc694 \uc6d0\uc778\\n\\n1. **\ube44\ud6a8\uc728\uc801\uc778 \ub370\uc774\ud130 \uc870\ud68c \ubc29\uc2dd**\\n\\n   ```php\\n   // \uae30\uc874: \ub2e8\uac74 \uc870\ud68c \ubc18\ubcf5\\n   for($i = 0; $i < count($list); $i++) {\\n       $sql = \\"SELECT id, member_info FROM \uace0\uac1d\ud14c\uc774\ube14 WHERE id = \'$user_id\'\\";\\n       // \ub9e4 \ubc18\ubcf5\ub9c8\ub2e4 DB \uc870\ud68c \ubc1c\uc0dd\\n       // \uace0\uac1d\uc774 \uc785\uacfc \ub418\uc5b4 \uc788\ub294 \ub370\uc774\ud130 \uc870\ud68c 1\\n       // for\ubb38 \uc548\uc5d0\uc11c \uace0\uac1d \uc815\ubcf4\uac00 \uc788\ub294 \ub610\ub2e4\ub978 \ub370\uc774\ud130 \uc870\ud68c\\n       // \uace0\uac1d \uc815\ubcf4\uac00 \uc788\ub294 \ub610\ub2e4\ub978 \ub370\uc774\ud130 \uc870\ud68c\\n\\n       // \uc774\ub7ec\ud55c \ubc29\uc2dd\uc73c\ub85c \ub370\uc774\ud130 \uc870\ud68c\uac00 \ubc18\ubcf5\ub418\uc5b4 \uc131\ub2a5 \uc800\ud558\\n   }\\n   ```\\n\\n2. **\ud2b8\ub79c\uc7ad\uc158 \uad00\ub9ac \ubbf8\ud761**\\n   - \uac74\ubcc4 \ucc98\ub9ac \ubc0f \ucee4\ubc0b\uc73c\ub85c \uc778\ud55c \uc624\ubc84\ud5e4\ub4dc\\n   - \ubd80\ubd84 \uc2e4\ud328 \uc2dc \ub370\uc774\ud130 \uc815\ud569\uc131 \ubb38\uc81c\\n\\n## \u26a1 \uac1c\uc120 \ubc29\uc548\\n\\n### 1. \ud83d\udcbe \ub370\uc774\ud130 \uc870\ud68c \ucd5c\uc801\ud654\\n\\n```php\\n// \uac1c\uc120: IN \uc808\uc744 \ud65c\uc6a9\ud55c \uc77c\uad04 \uc870\ud68c\\n$user_ids = array_column($list, \'user_id\');\\n$sql = \\"SELECT * FROM \uace0\uac1d\ud14c\uc774\ube14 WHERE id IN (\\" . implode(\',\', $user_ids) . \\")\\";\\n$result = sql_query($sql);\\n\\n// \uba54\ubaa8\ub9ac \uce90\uc2f1 \uad6c\ud604\\n$user_map = [];\\nwhile($row = sql_fetch_array($result)) {\\n    $user_map[$row[\'id\']] = $row;  // \uc0ac\uc6a9\uc790 ID\ub97c \ud0a4\ub85c \ud558\ub294 \uc5f0\uad00 \ubc30\uc5f4 \uc0dd\uc131\\n}\\n\\n// \uce90\uc2dc\ub41c \ub370\uc774\ud130 \ud65c\uc6a9 \uc608\uc2dc\\nforeach($list as $item) {\\n    $user_id = $item[\'user_id\'];\\n    if (isset($user_map[$user_id])) {\\n        $user_info = $user_map[$user_id];  // DB \uc870\ud68c \uc5c6\uc774 \uba54\ubaa8\ub9ac\uc5d0\uc11c \uc989\uc2dc \uc870\ud68c\\n        // \ucd94\uac00 \ucc98\ub9ac \ub85c\uc9c1...\\n    }\\n}\\n```\\n\\n#### \uba54\ubaa8\ub9ac \uce90\uc2f1 \ud6a8\uacfc\\n\\n- \ud83d\ude80 DB \uc870\ud68c \ud69f\uc218: 300\ud68c \u2192 1\ud68c\ub85c \uac10\uc18c\\n- \ud83d\udca1 \uc870\ud68c \uc18d\ub3c4: O(n) \u2192 O(1) \ubcf5\uc7a1\ub3c4\ub85c \uac1c\uc120\\n- \ud83d\udd04 \ub370\uc774\ud130 \uc7ac\uc0ac\uc6a9: \ub3d9\uc77c \ub370\uc774\ud130 \ubc18\ubcf5 \uc870\ud68c \uc81c\uac70\\n\\n### 2. \ud83d\udce6 Bulk Insert \uad6c\ud604\\n\\n```php\\nfunction bulk_insert($tableName, $columns, $values) {\\n    $sql = \\"INSERT INTO {$tableName} (\\" . implode(\',\', $columns) . \\") VALUES \\";\\n    $valueStrings = [];\\n    \\n    foreach($values as $row) {\\n        $valueStrings[] = \\"(\\" . implode(\',\', array_map(\'sql_escape\', $row)) . \\")\\";\\n    }\\n    \\n    $sql .= implode(\',\', $valueStrings);\\n    return sql_query($sql);\\n}\\n```\\n\\n### 3. \ud83d\udd04 \ud2b8\ub79c\uc7ad\uc158 \ucc98\ub9ac \uac1c\uc120\\n\\n```php\\nsql_begin_transaction();\\ntry {\\n    // \ub370\uc774\ud130 \uac80\uc99d\\n    $validated_data = processValidate($input_data);\\n    \\n    // Bulk Insert \uc2e4\ud589\\n    bulk_insert(\'score_table\', $columns, $validated_data);\\n    bulk_insert(\'history_table\', $columns, $validated_data);\\n    \\n    sql_commit();\\n} catch (Exception $e) {\\n    sql_rollback();\\n    log_error($e->getMessage());\\n    return [\'success\' => false, \'message\' => \'\ucc98\ub9ac \uc911 \uc624\ub958\uac00 \ubc1c\uc0dd\ud588\uc2b5\ub2c8\ub2e4.\'];\\n}\\n```\\n\\n## \ud83d\udcca \uac1c\uc120 \uacb0\uacfc\\n\\n| \uad6c\ubd84 | \ucc98\ub9ac \uc2dc\uac04 | \uba54\ubaa8\ub9ac \uc0ac\uc6a9\ub7c9 | DB \ubd80\ud558 |\\n|-----|---------|------------|---------|\\n| \uac1c\uc120 \uc804 | 300\ubd84 | \ub192\uc74c | \ub192\uc74c |\\n| \uac1c\uc120 \ud6c4 | 5\ucd08 | \ucd5c\uc801\ud654 | \ub0ae\uc74c |\\n\\n### \u2728 \uc8fc\uc694 \uac1c\uc120 \ud3ec\uc778\ud2b8\\n\\n1. **\ud83d\udd0b \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud638\ucd9c \ucd5c\uc18c\ud654**\\n   - 300 * 10\ud68c \u2192 10\ud68c\ub85c \uac10\uc18c\\n   - \uba54\ubaa8\ub9ac \uce90\uc2f1 \ud65c\uc6a9\\n\\n2. **\u26a1 \uc77c\uad04 \ucc98\ub9ac \ub3c4\uc785**\\n   - Bulk Insert\ub85c Insert \ucffc\ub9ac \ucd5c\uc18c\ud654\\n   - \ud2b8\ub79c\uc7ad\uc158 \ub2e8\uc704 \ucd5c\uc801\ud654\\n\\n3. **\ud83d\udcc8 \ucf54\ub4dc \ud488\uc9c8 \ud5a5\uc0c1**\\n   - \ubaa8\ub4c8\ud654 \ubc0f \ud568\uc218 \ubd84\ub9ac\\n   - \uc5d0\ub7ec \ucc98\ub9ac \uac15\ud654\\n   - \ub370\uc774\ud130 \uac80\uc99d \ub85c\uc9c1 \uccb4\uacc4\ud654\\n\\n## \ud83d\udcab \ub9c8\uce58\uba70\\n\\n> \uc774\ubc88 \ub9ac\ud329\ud1a0\ub9c1\uc744 \ud1b5\ud574 \uc5bb\uc740 \uad50\ud6c8:\\n\\n- \ud83c\udfaf \ub808\uac70\uc2dc \ucf54\ub4dc\ub3c4 \uc801\uc808\ud55c \ub9ac\ud329\ud1a0\ub9c1\uc73c\ub85c \ud070 \uc131\ub2a5 \uac1c\uc120\uc774 \uac00\ub2a5\\n- \ud83d\udca1 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc791\uc5c5 \ucd5c\uc801\ud654\uc758 \uc911\uc694\uc131\\n- \ud83c\udfd7\ufe0f \uccb4\uacc4\uc801\uc778 \ucf54\ub4dc \uad6c\uc870\uc758 \ud544\uc694\uc131\\n\\n\uc55e\uc73c\ub85c\ub3c4 \uc9c0\uc18d\uc801\uc778 \ucf54\ub4dc \uac1c\uc120\uacfc \ud559\uc2b5\uc744 \ud1b5\ud574 \ub354 \ub098\uc740 \uc2dc\uc2a4\ud15c\uc744 \ub9cc\ub4e4\uc5b4\uac00\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\n\\nLegacy..Legacy..Legacy.. \ub0b4\uac00 \ub9cc\ub4e4\ub550 \uc9c4\uc9dc \uc774\ub7f0 \ubb38\uc81c\uac00 \uc548\uc0dd\uae30\ub3c4\ub85d \ub178\ub825\ud574\uc57c \ud560 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n:::tip \ud83d\udca1 \ucc38\uace0\\n\uc2e4\uc81c \ud504\ub85c\ub355\uc158 \ud658\uacbd\uc5d0\uc11c \uac80\uc99d\ub41c \uc0ac\ub840\uc785\ub2c8\ub2e4. \uc720\uc0ac\ud55c \uc131\ub2a5 \uc774\uc288\uac00 \uc788\ub2e4\uba74 \uc774\ub7ec\ud55c \uc811\uadfc \ubc29\uc2dd\uc744 \uace0\ub824\ud574\ubcf4\uc138\uc694.\\n:::"}]}}')}}]);